import "influxdata/influxdb/schema"
import "strings"

from(bucket: "primary")
  |> range(start: -1w)
  |> filter(fn: (r) => r["_measurement"] == "twitter")
  |> schema.fieldsAsCols()
  |> map(fn: (r) => ({ 
  _time_reverse: - int(v: r._time),
  _value: "["+strings.substring(end: 10, start: 0, v: string(v: r._time))+ " " + strings.substring(end: 19, start: 11, v: string(v: r._time))+" UTC] " + r.user + ": " + r.content
   }))
